import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';

const Users = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { user: currentUser } = useAuth();

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/users');
      setUsers(response.data);
      setError('');
    } catch (err) {
      setError('Failed to load users');
      console.error('Error fetching users:', err);
    } finally {
      setLoading(false);
    }
  };

  const sendConnectionRequest = async (userId) => {
    try {
      await axios.post('/api/connections/request', { addresseeId: userId });
      // Refresh users to update connection status
      fetchUsers();
    } catch (err) {
      console.error('Error sending connection request:', err);
      alert(err.response?.data?.error || 'Failed to send connection request');
    }
  };

  const acceptConnection = async (requesterId) => {
    try {
      await axios.post('/api/connections/accept', { requesterId });
      fetchUsers();
    } catch (err) {
      console.error('Error accepting connection:', err);
      alert(err.response?.data?.error || 'Failed to accept connection');
    }
  };

  const rejectConnection = async (requesterId) => {
    try {
      await axios.post('/api/connections/reject', { requesterId });
      fetchUsers();
    } catch (err) {
      console.error('Error rejecting connection:', err);
      alert(err.response?.data?.error || 'Failed to reject connection');
    }
  };

  const getConnectionButton = (user) => {
    switch (user.connection_status) {
      case 'connected':
        return <span className="connection-status connected">Connected</span>;
      case 'pending_sent':
        return <span className="connection-status pending">Request Sent</span>;
      case 'pending_received':
        return (
          <div className="connection-actions">
            <button 
              className="btn btn-success btn-sm" 
              onClick={() => acceptConnection(user.id)}
            >
              Accept
            </button>
            <button 
              className="btn btn-danger btn-sm" 
              onClick={() => rejectConnection(user.id)}
            >
              Reject
            </button>
          </div>
        );
      case 'rejected':
        return <span className="connection-status rejected">Rejected</span>;
      default:
        return (
          <button 
            className="btn btn-primary btn-sm" 
            onClick={() => sendConnectionRequest(user.id)}
          >
            Connect
          </button>
        );
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  if (loading) {
    return (
      <div className="loading">
        <h3>Loading users...</h3>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-error">
        {error}
      </div>
    );
  }

  return (
    <div>
      <div style={{ marginBottom: '30px' }}>
        <h1>Find People to Connect With</h1>
        <p style={{ color: '#65676b', marginTop: '10px' }}>
          Discover and connect with other users on the platform
        </p>
      </div>

      {users.length === 0 ? (
        <div className="empty-state">
          <h3>No other users found</h3>
          <p>You're the first user on the platform!</p>
        </div>
      ) : (
        <div className="users-grid">
          {users.map((user) => (
            <div key={user.id} className="user-card">
              <div className="user-info">
                <div className="user-avatar">
                  {user.username.charAt(0).toUpperCase()}
                </div>
                <div className="user-details">
                  <h3 className="user-name">{user.username}</h3>
                  <p className="user-joined">Joined {formatDate(user.created_at)}</p>
                </div>
              </div>
              <div className="user-actions">
                {getConnectionButton(user)}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Users;
